const { app, Tray, Menu, dialog, Notification, BrowserWindow, ipcMain, nativeImage, screen, shell, globalShortcut } = require('electron');
const path = require('path');
const fs = require('fs');
const { spawn } = require('child_process');
const AutoLaunch = require('auto-launch');
const screenshot = require('screenshot-desktop');
const ffmpegPath = require('ffmpeg-static');
const { ensureMediaDirs, timestampName } = require('./utils/paths');
const { loadConfig, saveConfig } = require('./utils/config');

let tray = null;
let isRecording = false;
let recorderWindow = null;
let overlayWindow = null;
let ffmpegProc = null;
let currentMp4Path = null;
let settingsWindow = null;
let config = null;
let stopGuardTimer = null;

const TRAY_ICON_DATA_URL = 'data:image/png;base64,'
function createTray() {
  const image = nativeImage.createFromDataURL(TRAY_ICON_DATA_URL);
  tray = new Tray(image);
  tray.setToolTip('xScreen');
  const contextMenu = Menu.buildFromTemplate([
    { label: ' Ekran grnts al', click: takeScreenshot },
    { label: isRecording ? ' Kayd durdur' : ' Ekran kaydn balat', click: toggleRecording },
    { type: 'separator' },
    { label: ' Ayarlar', click: openSettingsWindow },
    { type: 'separator' },
    { label: 'Web', click: () => shell.openExternal('https://seyfooksck.dev') },
    { type: 'separator' },
    { label: 'k', click: () => app.quit() },
  ]);
  tray.setContextMenu(contextMenu);
}

function refreshTrayMenu() {
  if (!tray) return;
  const contextMenu = Menu.buildFromTemplate([
    { label: ' Ekran grnts al', click: takeScreenshot },
    { label: isRecording ? ' Kayd durdur' : ' Ekran kaydn balat', click: toggleRecording },
    { type: 'separator' },
    { label: ' Ayarlar', click: openSettingsWindow },
    { type: 'separator' },
    { label: 'Web', click: () => shell.openExternal('https://seyfooksck.dev') },
    { type: 'separator' },
    { label: 'k', click: () => app.quit() },
  ]);
  tray.setContextMenu(contextMenu);
}

async function takeScreenshot() {
  try {
    const { photos } = ensureMediaDirs(config?.baseDir || null);
    const fileName = timestampName('Screenshot', 'png');
    const filePath = path.join(photos, fileName);
    const img = await screenshot({ format: 'png' });
    fs.writeFileSync(filePath, img);
    showNotification('Ekran grnts kaydedildi', filePath);
  } catch (err) {
    dialog.showErrorBox('Ekran grnts hatas', String(err));
  }
}

function openSettingsWindow() {
  if (settingsWindow && !settingsWindow.isDestroyed()) {
    settingsWindow.show();
    settingsWindow.focus();
    return;
  }
  settingsWindow = new BrowserWindow({
    width: 960,
    height: 640,
    autoHideMenuBar: true,
    webPreferences: { nodeIntegration: true, contextIsolation: false }
  });
  settingsWindow.loadFile(path.join(__dirname, 'ui.html'));
  settingsWindow.on('closed', () => { settingsWindow = null; });
}

function toggleRecording() {
  if (isRecording) {
    stopRecording();
  } else {
    startRecording();
  }
}

function startRecording() {
  if (isRecording) return;
  isRecording = true;
  refreshTrayMenu();
  const engine = (config?.recorderEngine) || 'ffmpeg';
  if (engine === 'ffmpeg') {
    startRecordingFFmpeg();
  } else {
    startRecordingMedia();
  }
}

function startRecordingFFmpeg() {
  const { videos } = ensureMediaDirs(config?.baseDir || null);
  currentMp4Path = path.join(videos, timestampName('Recording', 'mp4'));
  const fr = (config?.frameRate) || 30;
  const args = ['-y', '-f', 'gdigrab', '-framerate', String(fr), '-i', 'desktop'];
  if (config?.audio && config?.audioDevice) {
    args.push('-f','dshow','-i',`audio=${config.audioDevice}`);
  }
  args.push('-c:v','libx264','-preset','veryfast','-pix_fmt','yuv420p','-r',String(fr));
  if (config?.audio && config?.audioDevice) args.push('-c:a','aac');
  args.push(currentMp4Path);

  ffmpegProc = spawn(ffmpegPath, args, { windowsHide: true, stdio: ['pipe', 'ignore', 'pipe'] });
  let ffmpegErr = '';
  ffmpegProc.stderr.on('data', d => { ffmpegErr += d.toString(); });
  ffmpegProc.on('error', (e) => dialog.showErrorBox('FFmpeg hatas', String(e)));
  ffmpegProc.on('close', (code) => {
    ffmpegProc = null;
    // Overlay'i kapat ve guard timer' temizle
    try { clearTimeout(stopGuardTimer); } catch {}
    try { if (overlayWindow && !overlayWindow.isDestroyed()) overlayWindow.close(); } catch {}
    if (code === 0) {
      showNotification('Ekran kayd kaydedildi', currentMp4Path);
      if (settingsWindow && !settingsWindow.isDestroyed()) settingsWindow.webContents.send('recording-changed', false);
    } else {
      dialog.showErrorBox('Kayt hatas', ffmpegErr || `ffmpeg exit ${code}`);
    }
    isRecording = false; refreshTrayMenu();
  });

  createOverlayWindow();
  if (settingsWindow && !settingsWindow.isDestroyed()) settingsWindow.webContents.send('recording-changed', true);
}

function startRecordingMedia() {
  const { videos } = ensureMediaDirs(config?.baseDir || null);
  currentMp4Path = path.join(videos, timestampName('Recording', 'mp4'));
  const fr = (config?.frameRate) || 30;
  const args = ['-y','-f','webm','-i','pipe:0','-fflags','+genpts','-c:v','libx264','-preset','veryfast','-pix_fmt','yuv420p','-r',String(fr),'-c:a','aac',currentMp4Path];
  ffmpegProc = spawn(ffmpegPath, args, { windowsHide: true, stdio: ['pipe', 'ignore', 'pipe'] });
  let ffmpegErr = '';
  ffmpegProc.stderr.on('data', d => { ffmpegErr += d.toString(); });
  ffmpegProc.on('error', (e) => dialog.showErrorBox('FFmpeg hatas', String(e)));
  ffmpegProc.on('close', (code) => {
    const wasRecording = isRecording;
    ffmpegProc = null;
    if (code === 0) {
      showNotification('Ekran kayd kaydedildi', currentMp4Path);
      if (settingsWindow && !settingsWindow.isDestroyed()) settingsWindow.webContents.send('recording-changed', false);
    } else if (!wasRecording) {
      dialog.showErrorBox('Kayt dntrme hatas', ffmpegErr || `ffmpeg exit ${code}`);
    }
    isRecording = false; refreshTrayMenu();
  });

  recorderWindow = new BrowserWindow({
    width: 400, height: 300, show: false, frame: false, transparent: true,
    webPreferences: { nodeIntegration: true, contextIsolation: false },
  });
  recorderWindow.loadFile(path.join(__dirname, 'recorder.html'));
  recorderWindow.on('closed', () => { recorderWindow = null; });
  recorderWindow.webContents.once('did-finish-load', () => {
    const payload = { frameRate: config?.frameRate || 30, audio: !!config?.audio };
    recorderWindow.webContents.send('start-recording', payload);
    createOverlayWindow();
    if (settingsWindow && !settingsWindow.isDestroyed()) settingsWindow.webContents.send('recording-changed', true);
  });
}

function createOverlayWindow() {
  const display = screen.getPrimaryDisplay();
  const overlayWidth = 160;
  const overlayHeight = 60;
  const margin = 16;
  const x = display.workArea.x + display.workArea.width - overlayWidth - margin;
  const y = display.workArea.y + display.workArea.height - overlayHeight - margin;

  overlayWindow = new BrowserWindow({
    width: overlayWidth,
    height: overlayHeight,
    x,
    y,
    frame: false,
    transparent: true,
    alwaysOnTop: true,
    skipTaskbar: true,
    resizable: false,
    movable: false,
    focusable: true,
    webPreferences: {
      nodeIntegration: true,
      contextIsolation: false,
    },
  });

  overlayWindow.loadFile(path.join(__dirname, 'overlay.html'));
  overlayWindow.on('closed', () => {
    overlayWindow = null;
  });
}

function stopRecording() {
  if (!isRecording) return;
  if (overlayWindow && !overlayWindow.isDestroyed()) {
    overlayWindow.webContents.send('will-stop');
  }
  const engine = (config?.recorderEngine) || 'ffmpeg';
  if (engine === 'ffmpeg') {
    try { if (ffmpegProc && ffmpegProc.stdin) { ffmpegProc.stdin.write('q'); } } catch {}
    clearTimeout(stopGuardTimer);
    stopGuardTimer = setTimeout(() => { try { if (ffmpegProc) ffmpegProc.kill('SIGINT'); } catch {} }, 3000);
  } else {
    if (recorderWindow && !recorderWindow.isDestroyed()) {
      recorderWindow.webContents.send('stop-recording');
    }
    clearTimeout(stopGuardTimer);
    stopGuardTimer = setTimeout(() => {
      try { if (overlayWindow && !overlayWindow.isDestroyed()) overlayWindow.close(); } catch {}
      try { if (recorderWindow && !recorderWindow.isDestroyed()) recorderWindow.close(); } catch {}
      finalizeRecordingAndConvert();
    }, 3000);
  }
}
async function finalizeRecordingAndConvert() {
  try {
    // stdin'i kapat, ffmpeg dosyay finalize etsin
    if (ffmpegProc && ffmpegProc.stdin) {
      try { ffmpegProc.stdin.end(); } catch {}
    }
  } catch (err) {
    dialog.showErrorBox('Kayt dntrme hatas', String(err));
    isRecording = false;
    refreshTrayMenu();
  }
}

function showNotification(title, body) {
  try {
    const n = new Notification({ title, body });
    n.show();
  } catch (_) {
    // baz ortamlar bildirim desteklemeyebilir
  }
}

function setupAutoLaunch() {
  const autoLauncher = new AutoLaunch({
    name: 'xScreen',
    path: process.execPath,
  });
  autoLauncher.isEnabled().then((enabled) => {
    const should = !!config?.autoLaunch;
    if (should && !enabled) autoLauncher.enable().catch(() => {});
    if (!should && enabled) autoLauncher.disable().catch(() => {});
  });
}

// IPC
ipcMain.on('recorder-chunk', (_evt, buf) => {
  if (ffmpegProc && ffmpegProc.stdin && buf && buf.length) {
    try { ffmpegProc.stdin.write(Buffer.from(buf)); } catch {}
  }
});

ipcMain.on('recorder-stopped', () => {
  if (overlayWindow && !overlayWindow.isDestroyed()) {
    overlayWindow.close();
  }
  if (recorderWindow && !recorderWindow.isDestroyed()) {
    recorderWindow.close();
  }
  clearTimeout(stopGuardTimer);
  finalizeRecordingAndConvert();
});
ipcMain.on('overlay-stop-clicked', () => {
  stopRecording();
});

// UI IPC
ipcMain.handle('get-config', async () => {
  return config;
});

ipcMain.handle('save-config', async (_e, newCfg) => {
  config = saveConfig(newCfg);
  setupAutoLaunch();
  setupShortcuts();
  return config;
});

ipcMain.handle('get-media-list', async () => {
  const { photos, videos } = ensureMediaDirs(config?.baseDir || null);
  function readDirSafe(dir, exts) {
    try {
      const files = fs.readdirSync(dir);
      return files
        .filter(f => exts.includes(path.extname(f).toLowerCase()))
        .map(f => {
          const p = path.join(dir, f);
          const st = fs.statSync(p);
          return { name: f, path: p, mtime: st.mtimeMs, size: st.size };
        });
    } catch {
      return [];
    }
  }
  const images = readDirSafe(photos, ['.png']);
  const vids = readDirSafe(videos, ['.mp4']);
  images.sort((a,b)=>b.mtime-a.mtime);
  vids.sort((a,b)=>b.mtime-a.mtime);
  return { images, videos: vids };
});

ipcMain.handle('open-in-folder', async (_e, p) => {
  shell.showItemInFolder(p);
  return true;
});

ipcMain.handle('open-file', async (_e, p) => {
  await shell.openPath(p);
  return true;
});

ipcMain.handle('delete-file', async (_e, p) => {
  try { fs.unlinkSync(p); return true; } catch { return false; }
});

function setupShortcuts() {
  globalShortcut.unregisterAll();
  const sc = config?.shortcuts || {};
  if (sc.enableScreenshot) {
    try { globalShortcut.register('CommandOrControl+Alt+S', takeScreenshot); } catch {}
  }
  if (sc.enableRecord) {
    try { globalShortcut.register('CommandOrControl+Alt+R', toggleRecording); } catch {}
  }
}

app.setLoginItemSettings && app.setLoginItemSettings({ openAtLogin: true });

const gotLock = app.requestSingleInstanceLock();
if (!gotLock) {
  app.quit();
} else {
  app.on('second-instance', () => {
    // tepsi uygulamas, pencere yok
  });

  app.whenReady().then(() => {
    config = loadConfig();
    ensureMediaDirs(config?.baseDir || null);
    setupAutoLaunch();
    setupShortcuts();
    createTray();
  });

  app.on('window-all-closed', (e) => {
    // tepsi uygulamas olduundan, pencere kapannca uygulamay ak tutuyoruz
    e.preventDefault();
  });

  app.on('will-quit', () => {
    try { globalShortcut.unregisterAll(); } catch {}
  });
}

// UI komutlar
ipcMain.handle('get-status', async () => ({ isRecording }));
ipcMain.handle('ui-take-screenshot', async () => {
  try {
    await takeScreenshot();
    return { ok: true };
  } catch (e) {
    return { ok: false, error: String(e) };
  }
});
ipcMain.handle('ui-toggle-recording', async () => {
  toggleRecording();
  return { isRecording };
});
